import csv
import re
from datetime import datetime


def escape_hive_string(value):
    """Escape special characters for Hive string literals."""
    if value is None:
        return r'\N'  # Hive NULL representation
    # Escape backslashes first
    value = str(value).replace('\\', '\\\\')
    # Escape newlines
    value = value.replace('\n', '\\n')
    value = value.replace('\r', '\\r')
    # Escape tabs
    value = value.replace('\t', '\\t')
    # Escape single quotes (for string literals)
    value = value.replace("'", "\\'")
    # If the string contains the delimiter, we should quote it
    # Also quote if it contains newline or carriage return
    if re.search(r'[\t\n\r,]', value):
        return f"'{value}'"
    return value


def process_csv_to_hive(input_csv, output_txt):
    with open(input_csv, 'r', encoding='utf-8') as csvfile, \
            open(output_txt, 'w', encoding='utf-8') as txtfile:

        reader = csv.DictReader(csvfile)

        # Write header (optional, Hive can create tables without header)
        # header = reader.fieldnames
        # txtfile.write('\t'.join(header) + '\n')

        for row in reader:
            # Process each field
            processed_row = []
            for field in reader.fieldnames:
                value = row.get(field, None)
                # Special handling for MD5 fields - they should not be escaped as they are hashes
                if field.endswith('_md5'):
                    processed_value = f"'{value}'" if value is not None else r'\N'
                else:
                    processed_value = escape_hive_string(value)
                processed_row.append(processed_value)

            # Write as tab-separated values
            txtfile.write(','.join(processed_row) + '\n')


